async function getWeather(query, API_KEY, geoLocation) {
    const toContract = (data) => {
        if (data.error) return data;
        const toDateContract = (dateString) => {
            let date = dayjs(dateString, 'YYYY-MM-DD hh-mm');
            return {
                full: date.format('dddd, MMMM D, h:mm A'),
                dayOfWeek: date.format('ddd'),
                date: date.format('dddd, MMMM D'),
                time: date.format('h A')
            };
        };

        const extractCondition = (condition, isDay = true) => {
            const suffix = isDay ? 'day' : 'night';
            return {
                name: condition.text,
                icon: condition.icon.match("[day|night]\/([0-9]+).png$")[1] + '-' + suffix
            };
        };

        const extractTemperatures = (day) => {
            return {
                mintemp: day.mintemp_f,
                maxtemp: day.maxtemp_f,
                humidity: "humidity" in day ? day.humidity : day.avghumidity,
                rain: "daily_chance_of_rain" in day ? day.daily_chance_of_rain : day.chance_of_rain,
                wind: Math.round(("wind_kph" in day ? day.wind_kph : day.maxwind_kph) * (5 / 18)),
            };
        };

        const { current, location } = data;
        const today = data.forecast.forecastday[0];
        return {
            location: {
                name: data.location.name,
                country: data.location.country,
                region: data.location.region
            },
            current: {
                date: toDateContract(location.localtime),
                ...extractCondition(current.condition, !!data.current.is_day),
                ...extractTemperatures(today.day),
                temp: current.temp_f,
                feelslike: current.feelslike_f
            },
            forecast: data.forecast.forecastday
                .map((forecast, index) => {
                    const day = forecast.day;
                    return {
                        date: toDateContract(forecast.date),
                        ...extractCondition(day.condition),
                        ...extractTemperatures(day),
                        temp: day.avgtemp_f,

                        hourly: forecast.hour
                            .filter(x => x.time.indexOf(forecast.date) === 0)
                            .map((x, hourIndex) => {
                                return {
                                    day: index,
                                    hour: hourIndex,
                                    temp: x.temp_f,
                                    feelslike: x.feelslike_f,
                                    date: toDateContract(x.time),
                                    ...extractCondition(x.condition),
                                    ...extractTemperatures(x)
                                };
                            })
                    }
                })

        }
    };

    const fetch = async (q) => {
        const { data } = await axios.get(WEATHER_API_URL, {
            params: {
                key: API_KEY,
                days: 7,
                aqi: 'no',
                alerts: 'no',
                q: q,
                lang: 'en'
            }
        }).catch(error => ({ data: { error } }));
        return toContract(data);
    }

    let city;
    const translations = weatherTranslations();
    const weatherWord = translations.filter(el => query.trim().toLowerCase() === el.toLowerCase());
    if (weatherWord.length) {
        if (geoLocation) {
            if (!geoLocation.localSearchEnabled && !geoLocation.coords) return { localSearchEnabled: false };
            else if (geoLocation.coords) city = `${geoLocation.coords.lat},${geoLocation.coords.lon}`;
            else if (geoLocation.city) city = geoLocation.city;
            else return { geoLocationFailed: true };
        }
    } else {
        city = extractCity(query)
    }

    return city ? await fetch(city) : null;
}

function weatherTranslations() {
    return ["weather", "temperature", "precipitation", "wind", "forecast", "rain", "het weer", "vejr", "hava", "météo", "meteo", "clima", "pogoda", "wetter", "tempo atmosferico", "väder", "vader", "počasí", "pocasi", "vær", "vaer", "mausam", "मौसम", "tiānqì", "tianqi", "天气", "nalssi", "날씨", "tenki", "天気"]
}

function extractCity(query) {
    query = query ? query.toLowerCase() : "";

    const translations = weatherTranslations();
    const filterWeatherWord = translations.filter(el => query.match(el))
    const weatherWord = filterWeatherWord.length ? filterWeatherWord[0] : "weather"
    const regexString = `\\b ${weatherWord}\\b`;
    const weatherRegex = new RegExp(regexString)
    // match the query with "... weather" at the end
    if (query.match(weatherRegex)) {
        return query.split(weatherRegex).join("");
    }

    const keywords = [weatherWord, ...["in", "near", "around", "by"].map((el) => `${weatherWord} ${el}`)];

    const isKeywordUsed = keywords.some((k, i) => {
        const regexKeyword = `\\b${k}\\b`;
        const keywordRegex = new RegExp(regexKeyword);
        return query.match(keywordRegex);
    });

    if (!isKeywordUsed) {
        return null;
    }

    const city = keywords.reduce((a, keyword, i) => a.replace(i === 0 ? keyword : keyword.split(weatherWord).join(""), ""), query).trim();
    return city;
}

async function trigger(query) {
    const translations = weatherTranslations();
    const weatherWord = translations.filter(el => query.trim().toLowerCase() === el.toLowerCase());
    return !!extractCity(query) || weatherWord.length;
}

module.exports = { weather, trigger };